import React,{useState, useEffect} from 'react'
import logo from './logo.svg';
import './App.css';

function App() {
    const [image,setImage] = useState("")
    const [url,setUrl] = useState(undefined)
    useEffect(()=>{
        if(url)
        {
            uploadFields()
        }
    },[url])

    const uploadPic = ()=>{
        const data = new FormData()
        data.append("file",image)
        data.append("upload_preset","image_classifier_images")
        data.append("cloud_name","duxnhitdq")

        // for pic upload
        fetch("https://api.cloudinary.com/v1_1/duxnhitdq/image/upload",{
            method:"post",
            body:data
            })
            .then(res=>res.json())
            .then(data=>{
                 console.log(data)
                setUrl(data.url)
            })
            .catch(err=>{
                console.log(err)
            })
    }

    const uploadFields=()=>{

        fetch("http://0.0.0.0:8181/my_api/classify",{
            method:"post",
            headers:{
                "Content-Type":"application/json"
            },
            body:JSON.stringify({
                pic:url
            })
        }).then(res=>res.json()).then(data=>{
            console.log(data)
            if(data.error){
                console.log({html: data.error,classes:"#c62828 red darken-3"})
            }
            else{
                console.log({html:data.message,classes:"#388e3c green darken-2"})
            }
        }).catch(err=>{
            console.log(err)
        })
    }

    const PostData = ()=>{

        if(image)
        {
            uploadPic()
        }
        else{
            uploadFields()
        }

    }
  return (
    <div className="App">
    <div className="file-field input-field">
                    <div className="btn .#6a1b9a purple darken-3">
                        <span>Upload Pic</span>
                        <input type="file" onChange={(e)=>setImage(e.target.files[0])}/>
                    </div>
                </div>
                <button className="btn waves-effect waves-light .#6a1b9a purple darken-3" onClick={()=>PostData()}>
                Upload
                </button>
    </div>
  );
}

export default App;
